export class XYLocation {
    x: number;
    y: number;
    constructor(x: number, y: number) {
        this.x = x;
        this.y = y;
    }
}

class SpaceframeOutlineHelper {

    draw(pdf: PDFDocument, spaceframe?: SpaceframeViewModel, serviceYear?: number) {
        if (spaceframe) {
            const outline = this.getOutline(spaceframe, serviceYear);
            if (outline) {
                this.drawOutline(pdf, outline, true);
            } else if (spaceframe.outline) {
                this.drawOutline(pdf, spaceframe.outline, false);
            } else {
                this.drawOutline(pdf, GALAXY_CLASS_OUTLINE, true);
            }
        } else {
            this.drawOutline(pdf, GALAXY_CLASS_OUTLINE, true);
        }
    }

    getOutline(spaceframe?: SpaceframeViewModel, serviceYear?: number) {
        if (spaceframe.id == null) {
            if (this.nameIsLike(spaceframe.name, "Loknar Class")) {
                return this.isMovieEra(serviceYear) ? LOKNAR_MOVIE_ERA_OUTLINE : LOKNAR_TOS_ERA_OUTLINE;
            } else if (this.nameIsLike(spaceframe.name, "Archer Class")) {
                return ARCHER_CLASS_OUTLINE;
            } else if (this.nameIsLike(spaceframe.name, "Ross Class")) {
                return ROSS_CLASS_OUTLINE;
            } else if (this.nameIsLike(spaceframe.name, "Parliament Class")) {
                return PARLIAMENT_CLASS_OUTLINE;
            } else if (this.nameIsLike(spaceframe.name, "Antares Class")) {
                return ANTARES_CLASS_OUTLINE;
            } else if (this.nameIsLike(spaceframe.name, "Miranda Class")) {
                return this.isMovieEra(serviceYear) ? MIRANDA_MOVIE_ERA : MIRANDA_TOS_ERA;
            } else {
                return GALAXY_CLASS_OUTLINE;
            }
        } else if (spaceframe.id === Spaceframe.Constitution && this.isMovieEra(serviceYear)) {
            return CONSTITUTION_MOVIE_ERA_OUTLINE;
        } else if (spaceframe.id === Spaceframe.Miranda && this.isMovieEra(serviceYear)) {
            return MIRANDA_MOVIE_ERA;
        } else if (spaceframe.id === Spaceframe.Miranda) {
            return MIRANDA_TOS_ERA;
        } else if (spaceframe.id === Spaceframe.Galaxy) {
            return GALAXY_CLASS_OUTLINE;
        } else if (spaceframe.id === Spaceframe.Hermes) {
            return HERMES_CLASS_OUTLINE;
        } else {
            return null;
        }
    }

    isMovieEra(serviceYear?: number) {
        return serviceYear && serviceYear >= 2270;
    }

    nameIsLike(name: string, comparison: string) {
        if (name) {
            name = name.toUpperCase().replace('-', ' ').trim();
            return name === comparison.toUpperCase() || (name + " CLASS") === comparison.toUpperCase();
        } else {
            return false;
        }
    }

    drawOutline(pdf: PDFDocument, outline: string, newfangled: boolean) {
        const orange = rgb(245.0/255, 157.0/255.0, 8.0/255.0);
        if (newfangled) {
            // the newfangled outlines are positioned into the upper-left hand corner, 
            // and need to be moved to the appropriate spot on the sheet.
            this.drawOutlineAtLocation(pdf, outline, orange, new XYLocation(43.5, 290.25));
        } else {
            // SVG edited using Inkscape end up using a px-based measures
            this.drawOutlineAtLocation(pdf, outline, orange, new XYLocation(0, 0));
        }
    }

    drawOutlineAtLocation(pdf: PDFDocument, outline: string, color: RGB, location: XYLocation) {
        const page = pdf.getPage(0);
        page.moveTo(location.x, page.getHeight() - location.y);
        page.drawSvgPath(outline, { borderColor: color, borderWidth: 1.6, scale: 0.75 })
    }
}

export const SpaceframeOutline = new SpaceframeOutlineHelper();