export abstract class BaseNeckProvider {


    protected getNeck(body: BodyType, skinColor: string, species?: Species, uniformEra?: UniformEra) {
        if (UniformVariantRestrictions.isStraightenedNeck(uniformEra)) {

            if (body === BodyType.AverageMale) {
                let result = Necks.straightenedMale.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                if (species === Species.Bolian) {
                    result += BolianRidge.straightenedMale;
                } else if (species === Species.Benzite) {
                    result += BenziteNeckAddOn.straightened.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                } else if (species === Species.XindiReptilian) {
                    result += "<g transform=\"translate(-10, 0)\">" + XindiReptilianNeckAddOn + "</g>";
                }
                return result;
            } else {
                let result = Necks.straightenedFemale.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                if (species === Species.Bolian) {
                    result += BolianRidge.straightenedFemale;
                } else if (species === Species.Benzite) {
                    result += BenziteNeckAddOn.straightened.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                } else if (species === Species.XindiReptilian) {
                    result += "<g transform=\"translate(-10, 0)\">" + XindiReptilianNeckAddOn + "</g>";
                }
                return result;
            }
        } else {

            if (species === Species.Caitian) {
                if (body === BodyType.AverageMale) {
                    return CaitianNecks.male.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                } else {
                    return CaitianNecks.female.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                }
            } else {
                if (body === BodyType.AverageMale) {
                    let result = Necks.averageMale.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                    if (species === Species.Bolian) {
                        result += TngEraMaleBodyBolianRidge
                    } else if (species === Species.Benzite) {
                        result += BenziteNeckAddOn.original.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                    } else if (species === Species.XindiReptilian) {
                        result += XindiReptilianNeckAddOn;
                    }
                    return result;
                } else {
                    let result = Necks.averageFemale.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                    if (species === Species.Bolian) {
                        result += TngEraFemaleBodyBolianRidge
                    } else if (species === Species.Benzite) {
                        result += BenziteNeckAddOn.original.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, skinColor);
                    } else if (species === Species.XindiReptilian) {
                        result += XindiReptilianNeckAddOn;
                    }
                    return result;
                }
            }
        }
    }
}