export class CivilianOutfitUniformPack extends BaseNeckProvider implements IUniformPack {

    getUniformSwatches() {
        return [
            new Swatch(BodyType.AverageMale, "Average Male", (token) => UniformCatalog.decorateSwatch(this.getNeck(BodyType.AverageMale, token.skinColor) + CivilianOutfits.maleInsurrectionLeatherJacket, BodyType.AverageMale, token), "BodyType.averageMale"),
            new Swatch(BodyType.AverageFemale, "Average Female", (token) => UniformCatalog.decorateSwatch(this.getNeck(BodyType.AverageFemale, token.skinColor) + CivilianOutfits.femalePicardLongSleeveWithCover, BodyType.AverageFemale, token), "BodyType.averageFemale"),
        ];
    }

    getRankSwatches() {
        return [
            new Swatch(Rank.None, "None", (token) => RankIndicatorCatalog.decorateSwatch("", Rank.None, token), "Rank.none.name"),
        ];
    }

    getRankIndicator(token: Token) {
        return "";
    }

    getRankBorderIndicator(token: Token) {
        return "";
    }

    getRankBorderDefinitions(token: Token, bordered: boolean) {
        return "";
    }

    getUniformAndVariantBody(token: Token) {
        let result = "";
        let neck = this.getNeck(token.bodyType, token.skinColor, token.species)
        switch (token.bodyType) {
            case BodyType.AverageMale:
                switch (token.variant) {
                    case UniformVariantType.Variant1:
                        result = CivilianOutfits.maleTosCaptainGarthJacket;
                        break;
                    default:
                        result = CivilianOutfits.maleInsurrectionLeatherJacket;
                    }
                break;
            case BodyType.AverageFemale:
            default:
                result = CivilianOutfits.femalePicardLongSleeveWithCover;
        }

        return (neck + result).replace(DefaultRed, token.divisionColor).replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, token.skinColor);
    }

    getBorderColor(token: Token) {
        return "#cccccc";
    }

    getUniformVariantSwatches(token: Token) {
        let swatches = [];
        if (token.bodyType === BodyType.AverageMale) {
            swatches.push(new Swatch(UniformVariantType.Base, "Default Outfit", (token) => UniformCatalog.decorateSwatch(this.getNeck(BodyType.AverageMale, token.skinColor) + CivilianOutfits.maleInsurrectionLeatherJacket, 1000 + UniformVariantType.Base, token)));
            swatches.push(new Swatch(UniformVariantType.Variant1, "Business Outfit", (token) => UniformCatalog.decorateSwatch(this.getNeck(BodyType.AverageFemale, token.skinColor) + CivilianOutfits.maleTosCaptainGarthJacket, 1000 + UniformVariantType.Variant1, token)));
            return swatches;
        } else {
            return [];
        }
    }

    getBorderLogo(token: Token): string {
        return "";
    }
}