class MouthCatalog {

    private static _instance: MouthCatalog;

    public static get instance() {
        if (MouthCatalog._instance == null) {
            MouthCatalog._instance = new MouthCatalog();
        }
        return MouthCatalog._instance;
    }

    getMouth(token: Token) {
        let result = ThinLip;
        if (token.species === Species.Tellarite && token.mouthType === MouthType.Mouth1) {
            result = TellariteMouth1;
        } else if (token.mouthType === MouthType.Mouth2) {
            result = MediumLip;
        } else if (token.mouthType === MouthType.Mouth3) {
            result = MediumLip2;
        } else if (token.mouthType === MouthType.Mouth4) {
            result = FullerLip;
        } else if (token.mouthType === MouthType.Mouth5) {
            result = FullerLip2;
        } else if (token.mouthType === MouthType.Mouth6) {
            result = FullerLip3;
        } else if (token.mouthType === MouthType.Mouth1Lipstick) {
            result = LipstickStarlet;
        } else if (token.mouthType === MouthType.Mouth2Lipstick) {
            result = LipstickFullLipped;
        } else if (token.mouthType === MouthType.Mouth3Lipstick) {
            result = LipstickProminentUpperLip;
        } else if (token.mouthType === MouthType.Mouth4Lipstick) {
            result = LipstickThinLipped;
        } else if (token.mouthType === MouthType.Mouth5Lipstick) {
            result = LipstickDownwardLips;
        }
        return result.replace(/#c14242/g, token.lipstickColor);
    }

    getSwatches(token: Token) {
        if (token.species === Species.Tellarite) {
            return [
                new Swatch(MouthType.Mouth1, "Thin Lip", (token) => MouthCatalog.decorateSwatch(TellariteMouth1, token)),
                new Swatch(MouthType.Mouth2, "Medium Lip", (token) => MouthCatalog.decorateSwatch(MediumLip, token)),
                new Swatch(MouthType.Mouth3, "Medium Lip Frowning", (token) => MouthCatalog.decorateSwatch(MediumLip2, token)),
                new Swatch(MouthType.Mouth4, "Slightly Fuller Lip", (token) => MouthCatalog.decorateSwatch(FullerLip, token)),
                new Swatch(MouthType.Mouth5, "Fairly Full Lip", (token) => MouthCatalog.decorateSwatch(FullerLip2, token)),
                new Swatch(MouthType.Mouth6, "Broad, Full Lip", (token) => MouthCatalog.decorateSwatch(FullerLip3, token)),

                new Swatch(MouthType.Mouth1Lipstick, "Lipstick, Classic Hollywood Starlet",(token) =>  MouthCatalog.decorateSwatch(LipstickStarlet, token)),
                new Swatch(MouthType.Mouth2Lipstick, "Lipstick, Fuller Pout", (token) => MouthCatalog.decorateSwatch(LipstickFullLipped, token)),
                new Swatch(MouthType.Mouth3Lipstick, "Lipstick, Prominent Upper Lip", (token) => MouthCatalog.decorateSwatch(LipstickProminentUpperLip, token)),
                new Swatch(MouthType.Mouth4Lipstick, "Lipstick, Thin Lip", (token) => MouthCatalog.decorateSwatch(LipstickThinLipped, token)),
                new Swatch(MouthType.Mouth5Lipstick, "Lipstick, Downward-pointing", (token) => MouthCatalog.decorateSwatch(LipstickDownwardLips, token)),
            ]
        } else {
            return [
                new Swatch(MouthType.Mouth1, "Thin Lip", (token) => MouthCatalog.decorateSwatch(ThinLip, token)),
                new Swatch(MouthType.Mouth2, "Medium Lip", (token) => MouthCatalog.decorateSwatch(MediumLip, token)),
                new Swatch(MouthType.Mouth3, "Medium Lip Frowning", (token) => MouthCatalog.decorateSwatch(MediumLip2, token)),
                new Swatch(MouthType.Mouth4, "Slightly Fuller Lip", (token) => MouthCatalog.decorateSwatch(FullerLip, token)),
                new Swatch(MouthType.Mouth5, "Fairly Full Lip", (token) => MouthCatalog.decorateSwatch(FullerLip2, token)),
                new Swatch(MouthType.Mouth6, "Broad, Full Lip", (token) => MouthCatalog.decorateSwatch(FullerLip3, token)),

                new Swatch(MouthType.Mouth1Lipstick, "Lipstick, Classic Hollywood Starlet",(token) =>  MouthCatalog.decorateSwatch(LipstickStarlet, token)),
                new Swatch(MouthType.Mouth2Lipstick, "Lipstick, Fuller Pout", (token) => MouthCatalog.decorateSwatch(LipstickFullLipped, token)),
                new Swatch(MouthType.Mouth3Lipstick, "Lipstick, Prominent Upper Lip", (token) => MouthCatalog.decorateSwatch(LipstickProminentUpperLip, token)),
                new Swatch(MouthType.Mouth4Lipstick, "Lipstick, Thin Lip", (token) => MouthCatalog.decorateSwatch(LipstickThinLipped, token)),
                new Swatch(MouthType.Mouth5Lipstick, "Lipstick, Downward-pointing", (token) => MouthCatalog.decorateSwatch(LipstickDownwardLips, token)),
            ]
        }
    }

    private static decorateSwatch(svg: string, token: Token) {
        let result = `<svg viewBox="0 0 100 100" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                <g transform="translate(-255, -140)">`
            + svg.replace(/#c14242/g, token.lipstickColor)
            + `</g>
            </svg>`;
        return result;
    }
}

export default MouthCatalog;