class MouthCatalog {

    private static _instance: MouthCatalog;

    public static get instance() {
        if (MouthCatalog._instance == null) {
            MouthCatalog._instance = new MouthCatalog();
        }
        return MouthCatalog._instance;
    }

    getMouth(token: Token) {
        return MouthCatalog.getMouthSvg(token.mouthType, token.species).replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, token.skinColor).replace(/#c14242/g, token.lipstickColor);
    }

    private static getMouthSvg(mouthType: MouthType, species: Species) {
        if (SpeciesRestrictions.isRubberHeaded(species)) {
            return "";
        } else if (species === Species.Tellarite) {
            let philtrum = TellariteMouths.Philtrum;
            let result = TellariteMouths.Mouth1;
            switch (mouthType) {
                case MouthType.Mouth2:
                    result = TellariteMouths.Mouth2;
                    break;
                case MouthType.Mouth3:
                    result = TellariteMouths.Mouth3;
                    break;
                case MouthType.Mouth4:
                    result = TellariteMouths.Mouth4;
                    break;
                case MouthType.Mouth5:
                    result = TellariteMouths.Mouth5;
                    break;
                case MouthType.Mouth6:
                    result = TellariteMouths.Mouth6;
                    break;
                case MouthType.Mouth7:
                    philtrum = "";
                    result = Pakled.Mouth7;
                    break;
                case MouthType.Mouth8:
                    philtrum = "";
                    result = Pakled.Mouth8;
                    break;
                case MouthType.Mouth1Lipstick:
                    result = TellariteMouths.Mouth1Lipstick;
                    break;
                case MouthType.Mouth2Lipstick:
                    result = TellariteMouths.Mouth2Lipstick;
                    break;
                case MouthType.Mouth3Lipstick:
                    result = TellariteMouths.Mouth3Lipstick;
                    break;
                case MouthType.Mouth4Lipstick:
                    result = TellariteMouths.Mouth4Lipstick;
                    break;
                case MouthType.Mouth5Lipstick:
                    result = TellariteMouths.Mouth5Lipstick;
                    break;
                case MouthType.Mouth6Lipstick:
                    result = TellariteMouths.Mouth6Lipstick;
                    break;
                default:
            }

            return result + philtrum;
        } else if (species === Species.Pakled) {
            let philtrum = StandardPhiltrum;

            let result = ThinLip;
            if (mouthType === MouthType.Mouth2) {
                result = MediumLip;
            } else if (mouthType === MouthType.Mouth3) {
                result = MediumLip2;
            } else if (mouthType === MouthType.Mouth4) {
                result = DefaultLip;
            } else if (mouthType === MouthType.Mouth5) {
                philtrum = PronouncedPhiltrum;
                result = FullerLip2;
            } else if (mouthType === MouthType.Mouth6) {
                philtrum = PronouncedPhiltrum;
                result = FullerLip3;
            } else if (mouthType === MouthType.Mouth7) {
                philtrum = "";
                result = Pakled.Mouth7;
            } else if (mouthType === MouthType.Mouth8) {
                philtrum = "";
                result = Pakled.Mouth8;
            } else if (mouthType === MouthType.Mouth1Lipstick) {
                philtrum = PronouncedPhiltrum;
                result = LipstickStarlet;
            } else if (mouthType === MouthType.Mouth2Lipstick) {
                philtrum = PronouncedPhiltrum;
                result = LipstickFullLipped;
            } else if (mouthType === MouthType.Mouth3Lipstick) {
                result = LipstickProminentUpperLip;
            } else if (mouthType === MouthType.Mouth4Lipstick) {
                result = LipstickThinLipped;
            } else if (mouthType === MouthType.Mouth5Lipstick) {
                result = LipstickDownwardLips;
            } else if (mouthType === MouthType.Mouth6Lipstick) {
                result = LipstickPronouncedCupidsBow;
            }

            return result + philtrum;
        } else {
            let philtrum = StandardPhiltrum;

            let result = ThinLip;
            if (mouthType === MouthType.Mouth2) {
                result = MediumLip;
            } else if (mouthType === MouthType.Mouth3) {
                result = MediumLip2;
            } else if (mouthType === MouthType.Mouth4) {
                result = DefaultLip;
            } else if (mouthType === MouthType.Mouth5) {
                philtrum = PronouncedPhiltrum;
                result = FullerLip2;
            } else if (mouthType === MouthType.Mouth6) {
                philtrum = PronouncedPhiltrum;
                result = FullerLip3;
            } else if (mouthType === MouthType.Mouth7) {
                philtrum = "";
                result = Pakled.Mouth7;
            } else if (mouthType === MouthType.Mouth8) {
                philtrum = "";
                result = Pakled.Mouth8;
            } else if (mouthType === MouthType.Mouth1Lipstick) {
                philtrum = PronouncedPhiltrum;
                result = LipstickStarlet;
            } else if (mouthType === MouthType.Mouth2Lipstick) {
                philtrum = PronouncedPhiltrum;
                result = LipstickFullLipped;
            } else if (mouthType === MouthType.Mouth3Lipstick) {
                result = LipstickProminentUpperLip;
            } else if (mouthType === MouthType.Mouth4Lipstick) {
                result = LipstickThinLipped;
            } else if (mouthType === MouthType.Mouth5Lipstick) {
                result = LipstickDownwardLips;
            } else if (mouthType === MouthType.Mouth6Lipstick) {
                result = LipstickPronouncedCupidsBow;
            }

            return result + philtrum;
        }
    }

    getSwatches(token: Token) {
        if (SpeciesRestrictions.isRubberHeaded(token.species)) {
            return [];
        } else {
            return [
                new Swatch(MouthType.Mouth1, "Thin Lip", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth1, token), "MouthType.mouth1"),
                new Swatch(MouthType.Mouth2, "Medium Lip", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth2, token), "MouthType.mouth2"),
                new Swatch(MouthType.Mouth3, "Medium Lip Frowning", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth3, token), "MouthType.mouth3"),
                new Swatch(MouthType.Mouth4, "Slightly Fuller Lip", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth4, token), "MouthType.mouth4"),
                new Swatch(MouthType.Mouth5, "Fairly Full Lip", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth5, token), "MouthType.mouth5"),
                new Swatch(MouthType.Mouth6, "Broad, Full Lip", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth6, token), "MouthType.mouth6"),
                new Swatch(MouthType.Mouth7, "Witless Pout", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth7, token), "MouthType.mouth7"),
                new Swatch(MouthType.Mouth8, "Witless, Teeth Showing", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth8, token), "MouthType.mouth8"),

                new Swatch(MouthType.Mouth1Lipstick, "Lipstick, Classic Hollywood Starlet",(token) =>  MouthCatalog.decorateSwatch(MouthType.Mouth1Lipstick, token), "MouthType.mouth1Lipstick"),
                new Swatch(MouthType.Mouth2Lipstick, "Lipstick, Fuller Pout", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth2Lipstick, token), "MouthType.mouth2Lipstick"),
                new Swatch(MouthType.Mouth3Lipstick, "Lipstick, Prominent Upper Lip", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth3Lipstick, token), "MouthType.mouth3Lipstick"),
                new Swatch(MouthType.Mouth4Lipstick, "Lipstick, Thin Lip", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth4Lipstick, token), "MouthType.mouth4Lipstick"),
                new Swatch(MouthType.Mouth5Lipstick, "Lipstick, Downward-pointing", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth5Lipstick, token), "MouthType.mouth5Lipstick"),
                new Swatch(MouthType.Mouth6Lipstick, "Lipstick, Pronounced Cupid's Bow", (token) => MouthCatalog.decorateSwatch(MouthType.Mouth6Lipstick, token), "MouthType.mouth6Lipstick"),
            ]
        }
    }

    private static decorateSwatch(mouthType: MouthType, token: Token) {
        let result = `<svg viewBox="0 0 100 100" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                <g transform="translate(-255, -140)">`
            + this.getMouthSvg(mouthType, token.species).replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, "#ffffff").replace(/#c14242/g, token.lipstickColor)
            + `</g>
            </svg>`;
        return result;
    }
}

export default MouthCatalog;